<style>
  .apps-container {
    height: 100%;
    background-color: #eee;
    text-align: center;

    padding-top: 50px;
  }

  .app-store-list {
    display: flex;
    flex-wrap: wrap;
    margin-top: 50px;
  }

  .app-store {
    color: #eee;
    background-color: #353b48;
    border-radius: 5px;
    margin: 20px;
    padding: 20px;

    min-width: 150px;

    position: relative;
    top: 0;

    -webkit-box-shadow: 3px 2px 9px 0px rgba(0,0,0,0.45);
		-moz-box-shadow: 3px 2px 9px 0px rgba(0,0,0,0.45);
    box-shadow: 3px 2px 9px 0px rgba(0,0,0,0.45);

    cursor: pointer;
    transition: .5s;
  }

  .app-store:hover {
    top: -10px;

    -webkit-box-shadow: 3px 2px 14px 0px rgba(0,0,0,0.45);
		-moz-box-shadow: 3px 2px 14px 0px rgba(0,0,0,0.45);
    box-shadow: 3px 2px 14px 0px rgba(0,0,0,0.45);

    transition: .5s;
  }

  .app-store.soon {
    background-color: #718093;
  }

  .app-store > h3 {
    text-transform: uppercase;
  }
</style>

